<!-- ********************************************************************************************************************** --><!-- Janela Pseudo Modal de Adicionar item  --><!-- ********************************************************************************************************************** --><div class="blocoComplementoItem text-center" style="display: none; padding: 0px;" id="modalAdicionarItemConfig"><br><br><div class="col-xs-12" style="margin-top: -25px;"><table class="table" style="margin: 0px;"><tr><td width="25%"><span style="font-size: 20px;">Quantidade:</span></td><td width="25%"><input type="number" id="modalQtdItem" class="form-control" value="1"><input type="hidden" value="3" id="qtd_n_cobrar"></td><td width="25%"><button class="btn btn-block btn-default" style="font-size: 20px;" onclick="calcQtdItem('+');"><i class="fa fa-plus"></i></button></td><td width="25%"><button class="btn btn-block btn-default" style="font-size: 20px;" onclick="calcQtdItem('-');"><i class="fa fa-minus"></i></button></td></tr><tr><td width="25%"><span style="font-size: 20px;">Valor Unitário:</span></td><td width="25%"><input type="text" id="modalVlrUnitarioItem" class="form-control" disabled><input id="modalVlrUnitarioItemReal" type="hidden" value="20"></td><td width="25%"><span style="font-size: 20px;">Valor Total:</span></td><td width="25%"><input type="text" id="modalVlrTotalItem" class="form-control" disabled></td></tr></table></div><div class="col-xs-12" style="margin: 0px; padding: 0px;"><!-- margin-top: -50px; --><!-- <br> --><!-- <div class="col-xs-2"><button class="btn btn-primary btn-block" style="padding: 0px;" onclick="adicionarNovoItem();"><i class="fa fa-file"></i><br> Novo</button></div> --><table class="table" style="margin: 0px;"><tr><td width="20%"><button class="btn btn-success btn-block" style="padding: 0px;" id="btn_salvar_item_ao_pedido" onclick="preAdicionaItemPedido()"><i class="fa fa-floppy-o"></i><br> <span style="font-size: 18px;">Salvar</span></button></td><td width="20%"><button type="button" class="btn btn-block btn-primary" style="padding: 0px;" onclick='mostrarComposicaoDoItem("Composicao", "Composição")'><i class="fa fa-list"></i><br> <span style="font-size: 18px;">Composição</span></button></td><td width="20%"><button type="button" class="btn btn-block btn-warning" style="padding: 0px;" onclick='mostrarComposicaoDoItem("Preparo", "Preparo")'><i class="fa fa-sign-language"></i><br> <span style="font-size: 18px;">Preparo</span></button></td><td width="20%"><button type="button" class="btn btn-block btn-success" style="padding: 0px;" onclick='mostrarComposicaoDoItem("Adicional", "Adicional")'><i class="fa fa-plus"></i><br> <span style="font-size: 18px;">Adicional</span></button></td><td width="20%"><script type="text/javascript">function fecharPseudoModalItem(){document.getElementById('modalAdicionarItemConfig').style.display='none';document.getElementById("codigoProduto").focus();/* $("#itemInputList-flexdatalist").val(""); */}</script><button class="btn btn-block btn-danger" style="padding: 0px;" onclick="fecharPseudoModalItem();"><i class="fa fa-times"></i><br> <span style="font-size: 18px;">Cancelar</span></button></td></tr></table><div id="operacoesItemModalAdicionar"></div></div><div class="col-xs-12" style="padding: 0px;margin-left: 0px;background-color: transparent; padding-right: 50px;margin-top: -20px;margin-bottom: 2px;"><br><div style="background-color: #337ab7;width: 100%;color: white;margin-left: 25px; border-style: solid;border-radius: 10px;"><span id="titulo_adicional_dos_itens" style="padding: 0;"></span></div><div class="hidden" id="conteudoComposicaoModalAdicionarItem" name="operacoesItemModalAdicionar"></div><div class="hidden" id="conteudoPreparoModalAdicionarItem" name="operacoesItemModalAdicionar"></div><div class="hidden" id="conteudoAdicionalModalAdicionarItem" name="operacoesItemModalAdicionar"></div><input type="hidden" id="modalIdItem"><input type="hidden" id="modalGrupoItem"><input type="hidden" id="modalSubGrupoItem"><input type="hidden" id="modalUnidadeMedidaItem"><!-- <h1>Adicional</h1><div class="blocoTabelaComplementoItem" style="padding-left: 30px;"><br><table class="table telaAdicional" style="margin: 0px;"><tr bgcolor="#f0ad4e"><td>Cebola</td><td><i class="fa fa-times"></i></td></tr><tr bgcolor="#5cb85c"><td>Queijo</td><td><i class="fa fa-check"></i></td></tr></table></div> --></div></div></div><script type="text/javascript">function adicionarNovoItem(){bootbox.confirm({title: "Tem certeza que deseja Adicionar um novo pedido?",message: "Ao aperta o botão \"Sim\" você irá adicionar um novo pedido e este item consecutivamente!",buttons: {confirm: {label: 'Sim',className: 'btn-success'},cancel: {label: 'Não',className: 'btn-danger'}},callback: function (result) {if (result) { adicionaPedido('item'); }}});}</script><style type="text/css">.telaAdicional{color: white;width: 90%;margin-left: 26px;margin-top: -25px;font-size: 20px;margin-right: -26px;}.blocoTabelaComplementoItem{bottom: 0px;margin-top: 0px;margin-left: 10px;height: 210px;width: 105%;overflow: auto;border-style: solid;border-radius: 10px;}.blocoComplementoItem{height: 70%;background-color: #fbfbfb;padding-right: 15px;box-shadow: 5px 5px 5px 5px #555;border-radius: 5px;width: 55%;position: absolute;margin-top: 180px;margin-left: 2%;/*margin-right: 2%;*/}.blocoComplementoItem_OLd{height: 80%;background-color: #fbfbfb;padding-right: 15px;margin-top: 10px;box-shadow: 5px 5px 5px 5px #555;border-radius: 5px;width: 22%;position: absolute;margin-top: 8%;margin-left: 45%;}</style>