<!-- ********************************************************************************************************************** --><!-- Teclado nÃºmerico --><!-- ********************************************************************************************************************** --><div id="tecladoNumerico" style="position: absolute; margin-top: 210px; width: 36%;margin-left: 61%; background-color: white; display: none;"><table class="table"><tr style="width: 33%"><td align="center"><button class="btn btn-block" onclick="acionarValorPesquisa('1')">&nbsp;1&nbsp;</button></td><td align="center"><button class="btn btn-block" onclick="acionarValorPesquisa('2')">&nbsp;2&nbsp;</button></td><td align="center"><button class="btn btn-block" onclick="acionarValorPesquisa('3')">3</button></td></tr><tr style="width: 33%"><td align="center"><button class="btn btn-block" onclick="acionarValorPesquisa('4')">&nbsp;4&nbsp;</button></td><td align="center"><button class="btn btn-block" onclick="acionarValorPesquisa('5')">&nbsp;5&nbsp;</button></td><td align="center"><button class="btn btn-block" onclick="acionarValorPesquisa('6')">6</button></td></tr><tr style="width: 33%"><td align="center"><button class="btn btn-block" onclick="acionarValorPesquisa('7')">&nbsp;7&nbsp;</button></td><td align="center"><button class="btn btn-block" onclick="acionarValorPesquisa('8')">&nbsp;8&nbsp;</button></td><td align="center"><button class="btn btn-block" onclick="acionarValorPesquisa('9')">9</button></td></tr><tr><td align="center"><button class="btn btn-block" onclick="corrigirValorPesquisa()"><i class="fa fa-arrow-left"></i></button></td><td align="center"><button class="btn btn-block" onclick="acionarValorPesquisa('0')">0</button></td><td align="center"><button class="btn btn-block" onclick="concluirValor();"><i class="fa fa-check"></i></button></td></tr></table></div><script type='text/javascript'>function acionarValorPesquisa(valor){document.getElementById('pesquisaPedido').value += valor;document.getElementById('pesquisaPedido').value	= document.getElementById('pesquisaPedido').value.substring(0,10);montarListaPedido($("#filial").val());}function corrigirValorPesquisa(){valor = document.getElementById('pesquisaPedido').value;document.getElementById('pesquisaPedido').value	= valor.substring(0,valor.length-1);montarListaPedido($("#filial").val());}function concluirValor(){document.getElementById("pesquisaPedido").blur();esconderTacladoNum();}function mostrarTecladoNum(){document.getElementById('tecladoNumerico').style.display = "block";}function esconderTacladoNum(){document.getElementById('tecladoNumerico').style.display = "none";}</script>